def extract_information(text):
    """Extract information from a text."""
    name = ""
    age = 0
    profession = ""
    hobby = ""
    for word in text.split():
        if word.isalpha():
            name += word
        elif word.isdigit():
            age = int(word)
        elif word.isspace():
            hobby += word
        else:
            profession += word
    return f"{name}, {age}, {profession}, {hobby}"
